import { Client } from './Api/Api'



/** 
 * @namespace 
 * @global
 * */
const ODA = {

  /**
   * Create client instance, 
   * @function
   * @async
   * @returns {Client}
   */
  createClient: async (options) => {
    const client = new Client(options)
    if (options.APIToken) {
      await client.loginWithToken(options.APIToken)
    }
    return client
  },
}

export default ODA;
